package OneHundredDaysOfCoding;import java.util.Scanner;public class gabut {public static void main(String[] args) {Scanner i = new Scanner(System.in);boolean lanjut = true;System.out.println("Deretan Alfabet Dari A-Z.");while (lanjut) {char ascii = 65;System.out.print("Panjang: ");int a = i.nextInt();for (int j = 1; j <= a; j++) {System.out.print(ascii + " ");if (j % 7 == 0) System.out.println();if (ascii > 90) ascii = 65;ascii++;}System.out.print("\nIngin mengulang? (true/false): ");lanjut = i.nextBoolean();}}}
